namespace problems;


class FindMaxConsecutiveOnes
{
    public function solution($nums)
    {
        $count = [0];
        $n = count($nums);
        for ($i =0; $i<$n; $i++) {
            if ($nums[$i] == 1) {
                $j = $i+1;
                $tmp = 1;
                while ($j < $n) {
                    if ($nums[$j] == 1) {
                        $tmp++;
                        $j++;
                        $i++;
                    } else {
                        break;
                    }
                }
                $count[] = $tmp;
            }
        }

        return max($count);
    }

    public function solution_2($nums)
    {
        $max = $count = 0;
        foreach ($nums as $k=>$num) {
            if ($num != 1) {
                if ($count > $max) {
                    $max = $count;
                }
                $count = 0;
            } else {
                $count++;
            }
        }

        return $max > $count ? $max : $count;
    }
}

