<?php


namespace problems;


class RemoveDuplicates
{
    public function solution($S)
    {
        $n = strlen($S)-1;
        for ($i=0; $i<(strlen($S)-1); $i++) {
            if ($S[$i] == $S[$i+1]) {
                $S = substr($S, 0, $i) . substr($S, $i+2, $n);
                $i -= 2;
                $n -= 2;
                if ($i < 0) { $i = -1; }
            }
        }
        return $S;
    }
}

$re = new RemoveDuplicates();
$tests = [
    'abbaca',
    'aaaaaaaa',
    ''
];

foreach ($tests as $test) {
    $res = $re->solution($test);
    var_dump($res);
}